INSERT INTO sjl_libros(
	isbn, titulo_esp, titulo_ori, tema_princ, sinop, n_pag, fec_pub, autor, id_prev, id_edit)
	VALUES (9780945353645, 'Los Diez Negritos', 'And Then There Were None', 
    'La isla del negro quedo en manos de un dueño misterioso que está empeñado en cometer espeluznantes asesinatos',
    'Ocho personas reciben una invitación para pasar unas vacaciones en la isla del Negro.​ Esta isla se encuentra en la costa inglesa, en la región de Devon. La invitación es sumamente atractiva, ya que se trata de una pequeña isla en la que solo existe una hermosa y gran mansión que fue construida por un millonario estadounidense, quien luego la vendió y nadie sabe hoy quién es el nuevo dueño. A cada uno de los invitados les llega la invitación por carta, firmada por una persona que dice haberlos conocido en el pasado en una situación que para todos es familiar, aunque no recuerdan bien a la persona en cuestión. Sin embargo, todos aceptan gustosos la invitación, ya que la persona que los invita describe detalladamente el lugar y las circunstancias en que los conoció. También porque en la nota aparece como una persona muy afectuosa y amable, y además, por lo irresistible que resulta el pasar unas vacaciones en una mansión tan lujosa situada en un lugar tan hermoso.',
    165, '11-06-1939', 'Agatha Christie', null, 11);

INSERT INTO sjl_libros(
