INSERT INTO sjl_libros(
	isbn, titulo_esp, titulo_ori, tema_princ, sinop, n_pag, fec_pub, autor, id_prev, id_edit)
	VALUES (78525, 'En el nombre de la rosa', 'Il nome della rosa', 
    'El nombre de la rosa tiene dos tramas paralelas que transcurren dentro de la misma Abadía.',
    'La historia nos es contada en primera persona por Adso, el amanuense y discípulo de Guillermo de Baskerville. Una serie de asesinatos de monjes y la labor detectivesca de Guillermo por descubrir al autor y por otro lado se produce en la Abadía reuniones con el fin de debatir sobre la marcha de la Orden franciscana y de la Iglesia.', 
    512, '01-01-1980', 'Umberto Eco', null, 13);

INSERT INTO sjl_libros(
	isbn, titulo_esp, titulo_ori, tema_princ, sinop, n_pag, fec_pub, autor, id_prev, id_edit)
	VALUES (99876, 'Los diez negritos', 'And Then There Were None', 
    'La isla del negro quedo en manos de un dueño misterioso que está empeñado en cometer espeluznantes asesinatos', 
    'Ocho personas —el juez Lawrence Wargrave, la institutriz Vera Claythorne, el aventurero Philip Lombard, el general retirado John MacArthur, la solterona Emily Brent, el playboy Anthony Marston, el doctor Edward Armstrong y el detective privado y expolicía William Blore— reciben una invitación para pasar unas vacaciones en la isla del Negro.​ Esta isla se encuentra en la costa inglesa, en la región de Devon.',
    165, '11-06-1993', 'Agatha Christie', null, 11);

INSERT INTO sjl_libros(
	isbn, titulo_esp, titulo_ori, tema_princ, sinop, n_pag, fec_pub, autor, id_prev, id_edit)
	VALUES (79561, 'Asesinato en el Orient Express', 'Murder in the Calais Coach', 'Durante el viaje, uno de los pasajeros, Mr. Ratchett, un millonario estadounidense, dice a Poirot que su vida está en peligro. Le muestra una pequeña pistola que lleva siempre consigo para protegerse.', 
    'El famoso detective privado Hércules Poirot regresa al Reino Unido a bordo del Orient Express desde Estambul. En esa época del año, en pleno invierno, el tren suele ir casi vacío, pero en esa ocasión va inusualmente lleno y Poirot solo consigue una litera gracias a su amigo monsieur Bouc, director de la Compagnie Internationale des Wagons-Lits. Cuando un tal Sr. Harris no se presenta, Poirot ocupa su lugar, junto a un joven estadounidense llamado Héctor MacQueen. La segunda noche, Poirot consigue un compartimento para él solo, que le cede monsieur Bouc.',
    256, '01-01-1934', 'Agatha Christie', null, 11);

INSERT INTO sjl_libros(
	isbn, titulo_esp, titulo_ori, tema_princ, sinop, n_pag, fec_pub, autor, id_prev, id_edit)
	VALUES (56987, 'La caída de los gigantes', 'The fall of giants', 'Guerra', 
    'La acción transcurre entre el 22 de junio de 1911, día en que fue coronado Jorge V en la abadía de Westminster, y finales de 1924, varios años después de finalizar la I Guerra Mundial. Se mezclan personajes históricos reales como el propio Rey Jorge V, Sir Edward Grey (secretario del Foreign Office) y Winston Churchill, con otros de ficción como Billy Williams, que trabaja desde los 13 años en las minas de carbón de Gales, y su padre David Williams destacado miembro del sindicato minero. ', 
    1017, '09-28-2010', 'Ken Follet', null, 14);

INSERT INTO sjl_libros(
	isbn, titulo_esp, titulo_ori, tema_princ, sinop, n_pag, fec_pub, autor, id_prev, id_edit)
	VALUES (68821, 'El invierno del mundo', 'Winter of the world', 'Guerra', 
    'En 1933, Berlín es un foco de agitación política y social. Lady Maud, ahora la esposa de Walter von Ulrich y madre de dos hijos, publica artículos en una revista semanal que ridiculizan al partido nazi mientras su marido manifiesta su oposición en el gobierno. Sin embargo, parece que nada podrá frenar el poder ascendente del canciller Adolf Hitler. Cuando Maud recibe la visita de Ethel Williams y su hijo Lloyd, todos serán testigos de la tiranía y la represión de la nueva Alemania. El reino del Tercer Reich se extenderá hasta Francia y más allá de la frontera rusa. '
    , 940, '09-18-2012', 'Ken Follet', 56987, 14);

INSERT INTO sjl_libros(
	isbn, titulo_esp, titulo_ori, tema_princ, sinop, n_pag, fec_pub, autor, id_prev, id_edit)
	VALUES (02145, 'Las ventajas de ser invisible', 'The Perks of Being a Wallflower', 
    'Charlie (Logan Lerman) es un estudiante del primer año de preparatoria. Es muy introvertido y lento haciendo amigos, termina haciéndose amigo de su profesor de literatura',
    'Charlie, un adolescente de 15 años, está a punto de comenzar su primer año de preparatoria en una nueva escuela. Su mejor amigo, Michael, se había suicidado un mes antes de que comenzara la narrativa. En un intento de lidiar con la muerte de su amigo, así como su ansiedad causada por el hecho de tener que asistir a la preparatoria solo',
    213, '02-01-1999', 'Stephen Chbosky', null, 16);

INSERT INTO sjl_libros(
	isbn, titulo_esp, titulo_ori, tema_princ, sinop, n_pag, fec_pub, autor, id_prev, id_edit)
	VALUES (89986, '100 años de Soledad', '100 años de Soledad', 'Soledad, Realismo magico, incesto',
    'José Arcadio Buendía y Úrsula Iguarán son un matrimonio de primos que se casaron llenos de presagios y temores por su parentesco y el mito existente en la región de que su descendencia podía tener cola de cerdo. En una pelea de gallos en la que resultó muerto el animal de Prudencio Aguilar, este, enardecido por la derrota, le gritó a José Arcadio Buendía, dueño del vencedor: «A ver si ese gallo le hace el favor a tu mujer», ya que la gente del pueblo sospechaba que José Arcadio y Úrsula no habían tenido relaciones en un año de matrimonio (por el miedo de Úrsula de que la descendencia naciera con cola de cerdo).'
    , 471, '01-01-1982', 'Gabriel Garcia Marquez', null, 6);

INSERT INTO sjl_libros(
	isbn, titulo_esp, titulo_ori, tema_princ, sinop, n_pag, fec_pub, autor, id_prev, id_edit)
	VALUES (58992, '1984', '1984', 'Estado vigilante, derechos humanos, libertad intelectual, totalitarismo y distopía',
    'El personaje principal de la novela es Winston Smith, que trabaja en el Ministerio de la Verdad. Su cometido es reescribir la historia, ironizando así el ideal declarado en el nombre del Ministerio. Tras años trabajando para dicho Ministerio, Winston Smith se va volviendo consciente de que los retoques de la historia en los que consiste su trabajo son solo una parte de la gran farsa en la que se basa su gobierno, y descubre la falsedad intencionada de todas las informaciones procedentes del Partido Único. En su ansia de evadir la omnipresente vigilancia del Gran Hermano (que llega inclusive a todas las casas) encuentra el amor de una joven rebelde llamada Julia, también desengañada del sistema político; ambos encarnan así una resistencia de dos contra una sociedad que se vigila a sí misma.',
    326, '01-01-1948', 'George Orwell', null, 50);

INSERT INTO sjl_libros(
	isbn, titulo_esp, titulo_ori, tema_princ, sinop, n_pag, fec_pub, autor, id_prev, id_edit)
	VALUES (87515, 'La rebelión en la granja', 'Animal Farm', 'Totalitarismo y distopia',
    'Los animales de la Granja Solariega, alentados un día por el Viejo Mayor, un cerdo que antes de morir les explicó a todos sus ideas, llevan a cabo una revolución en la que consiguen expulsar al granjero Howard Jones y crear sus propias reglas (los Siete Mandamientos)', 
    122, '08-17-1945', 'George Orwell', null, 17);

INSERT INTO sjl_libros(
	isbn, titulo_esp, titulo_ori, tema_princ, sinop, n_pag, fec_pub, autor, id_prev, id_edit)
	VALUES (48915, 'Un mundo feliz', 'Brave New World', 'Distopia', 'La novela comienza con un grupo de estudiantes que visitan la central de condicionamiento de Londres, donde un científico les muestra la técnica de la reproducción artificial. Por medio de ella comprenden que la organización de la sociedad es determinada desde el nacimiento. El estado mundial manipula la reproducción para garantizar personas perfectamente adaptadas a su posición social, designados con letras del alfabeto griego desde los Alfa, destinados a la dirigencia, hasta los Épsilon, diseñados para las tareas más peligrosas y repetitivas. Esta planificación de tipo genética se complementa mediante el condicionamiento por medio de la hipnopedia, la enseñanza durante el sueño, que inculca consignas y la educación.',
    256, '01-01-1932', 'Aldous Huxley', null , 50);

INSERT INTO sjl_libros(
	isbn, titulo_esp, titulo_ori, tema_princ, sinop, n_pag, fec_pub, autor, id_prev, id_edit)
	VALUES (98564, 'El viejo y el mar', 'The Old Man and the Sea', 'Muerte, lealtad, lucha contra la adversidad, constancia y valentia',
    'La obra se desarrolla en el Gulf Stream - Habana, cuyo protagonista es Santiago, aunque todo el mundo lo llama El viejo. Santiago es un pescador de avanzada edad, que lleva 84 días sin conseguir pesca alguna. Un buen día por la mañana, decide salir solo al mar, donde por fin, un enorme marlín (pez vela similar al pez espada) pica el anzuelo no sin dar batalla antes de ser capturado definitivamente.',
    127, '03-15-1953', 'Ernest Hemingway', null, 18);

INSERT INTO sjl_libros(
	isbn, titulo_esp, titulo_ori, tema_princ, sinop, n_pag, fec_pub, autor, id_prev, id_edit)
	VALUES (55210, 'Historia de dos ciudades', 'A Tale of Two Cities', 'Critica social',
    'La novela comienza con la joven Lucía Manette que, junto al señor Lorry (antiguo empleado de su padre), rescata a su padre a quien creía muerto y lo lleva a vivir a Inglaterra, tras pasar 18 años recluido y olvidado en la prisión de la Bastilla, donde fue encerrado en el calabozo 105 de la torre norte por la sanguinaria familia de los Saint Evremont.',
    526, '11-26-1859', 'Charles Dickens', null, 19);

INSERT INTO sjl_libros(
	isbn, titulo_esp, titulo_ori, tema_princ, sinop, n_pag, fec_pub, autor, id_prev, id_edit)
	VALUES (55200, 'Crimen y castigo', 'Преступле́ние и наказа́ние', 'Asesinato y angustia',
    'La historia narra la vida de Rodión Raskólnikov, un estudiante en la capital de la Rusia Imperial, San Petersburgo. Este joven se ve obligado a suspender sus estudios por la miseria en la cual se ve envuelto, a pesar de los esfuerzos realizados por su madre Pulqueria y su hermana Dunia para enviarle dinero. Necesitado de financiación para pagar sus gastos, había recurrido a una anciana prestamista vil y egoísta, en cuya casa empeña algunos objetos de valor.',
    426, '01-01-1866', 'Fiodor Dostoyevski', null, 50);

INSERT INTO sjl_libros(
	isbn, titulo_esp, titulo_ori, tema_princ, sinop, n_pag, fec_pub, autor, id_prev, id_edit)
	VALUES (27136, 'Orgullo y prejuicio', 'Pride and Prejudice', 'Conocimiento de si mismo, felicidad, feminidad',
    'En el centro de esta sociedad se encuentra la familia Bennet, con sus cinco hijas casaderas, de entre quince y veintitrés años (de mayor a menor: Jane, Elizabeth, Mary, Catherine y Lydia). La señora Bennet ve el matrimonio como la única esperanza para sus hijas, pues tras la muerte del señor Bennet las jóvenes quedarán abandonadas a su suerte cuando William Collins, primo de las muchachas, herede todo debido a que la propiedad forma parte de un mayorazgo del que es beneficiario.',
    569, '01-28-1953', 'Jane Austen', null, 20);

INSERT INTO sjl_libros(
	isbn, titulo_esp, titulo_ori, tema_princ, sinop, n_pag, fec_pub, autor, id_prev, id_edit)
	VALUES (58892, 'El gran Gatsby', 'The Great Gatsby', 'Sarah Churchwell comprende El gran Gatsby como una "advertencia de lo decrépito del sueño americano."',
    'Los principales acontecimientos de la novela se llevan a cabo en el verano de 1922. Nick Carraway, un nativo del Medio Oeste y graduado de Yale, veterano de la Primera Guerra Mundial -que sirve como narrador de la novela- acepta un trabajo en Nueva York como vendedor. Alquila una pequeña casa en Long Island, en el pueblo (ficticio) de West Egg, al lado de la lujosa mansión de Jay Gatsby, un misterioso millonario que organiza fiestas extravagantes, pero no participa en ellas.',
    218, '04-10-1925','F. Scott Fitzgerald', null, 18);

INSERT INTO sjl_libros(
	isbn, titulo_esp, titulo_ori, tema_princ, sinop, n_pag, fec_pub, autor, id_prev, id_edit)
	VALUES (68489, 'Ana Karenina', 'Áнна Карéнина', 'Critica social',
    'Al comienzo de la primera parte, el príncipe Stepán Arkádievich Oblonsky («Stiva»), personaje principal y alto funcionario que ha sido infiel a su mujer Darya Aleksándrovna («Dolly»), llama a su hermana, Anna Karénina, que reside en San Petersburgo, para que convenza a Dolly de que no lo abandone.',
    966, '01-01-1877', 'Leon Tolstoi', null, 21);